package com.example.shoppingcart.repositories;

import androidx.lifecycle.LiveData;
import androidx.lifecycle.MutableLiveData;

import com.example.shoppingcart.models.Product;

import java.util.ArrayList;
import java.util.List;
import java.util.UUID;

public class ShopRepo {

    private MutableLiveData<List<Product>> mutableProductList;

    public LiveData<List<Product>> getProducts() {
        if (mutableProductList == null) {
            mutableProductList = new MutableLiveData<>();
            loadProducts();
        }
        return mutableProductList;
    }

    private void loadProducts() {
        List<Product> productList = new ArrayList<>();
        productList.add(new Product(UUID.randomUUID().toString(), "iMac 21", 12990, false, "https://cdn.vox-cdn.com/thumbor/yvDASMJR59UMVlIo3UxqVBeV8bs=/0x0:2040x1360/1200x800/filters:focal(857x517:1183x843)/cdn.vox-cdn.com/uploads/chorus_image/image/65975270/akrales_191112_3778_0126.0.jpg"));
        productList.add(new Product(UUID.randomUUID().toString(), "Samsung Galaxy S8", 79990, true, "https://cdn.pocket-lint.com/r/s/1200x/assets/images/140644-phones-review-samsung-galaxy-s8-review-image1-wyesoelc2u.jpg"));
        productList.add(new Product(UUID.randomUUID().toString(), "One Plus", 38500, true, "https://m.media-amazon.com/images/I/612ytK4luvL._SL1500_.jpg"));
        productList.add(new Product(UUID.randomUUID().toString(), "iPhone 11", 85000, true, "https://store.storeimages.cdn-apple.com/4982/as-images.apple.com/is/iphone11-select-2019-family?wid=882&hei=1058&fmt=jpeg&qlt=90&.v=1567022175704"));
        productList.add(new Product(UUID.randomUUID().toString(), "Redmi Note 8", 15500, true, "data:image/jpeg;base64,"));
        productList.add(new Product(UUID.randomUUID().toString(), "iPhone SE", 35000, true, "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQY6Ct79EJVyKLMKWn8PKz2aMS2nMpjAtcSuA&usqp=CAU"));
        productList.add(new Product(UUID.randomUUID().toString(), "VIVO", 39500, true, "data:image/jpeg;base64,"));

        mutableProductList.setValue(productList);
    }
}
